import React from 'react'
import { use } from 'xadmin';

const LogoComponent = (props) => {
  const { settings = {} } = use('settings')
  return (
    <>
      <img src={settings.logo} className="guideImg"></img>
      <h3 className="guideTitle" dangerouslySetInnerHTML={{ __html: settings && settings['name'] }} />
    </>
  )
}

export default LogoComponent
