import React from 'react'
import { use } from 'xadmin'
import { _t } from 'xadmin-i18n'

const UserNameComponent = (props) => {
  const { user } = use('auth.user')

  return <div>{user && user.username}</div>
}

const paramSchema = {
  type: 'object',
  properties: {}
}

const UserName = {
  title: _r('用户名称'),
  category: ['页面元素', '导航栏'],
  icon: require('../icons/用户名称.svg'),
  component: UserNameComponent,
  initLayout: { width: 80, height: 40 },
  paramSchema,
  theme: [
    { selector: 'div', title: _r('用户名称样式') }
  ]
}
export default UserName