import React from "react"
import { Table } from 'antd'

const CompareResult = ({ result }) => {
  const columns = [
    { title: '结果名称', dataIndex: 'name', key: 'name' },
    { title: '输入参数', dataIndex: 'input', key: 'input' },
    { title: '输出参数', dataIndex: 'output', key: 'output' },
    { title: '调试时间', dataIndex: 'createTime', key: 'createTime' }
  ]
  return result?.length > 0 && <Table
    bordered={true}
    size="small"
    pagination={false}
    dataSource={result} 
    columns={columns}
    scroll={{ x: 300 }}
  />
}

export default CompareResult
