import React from 'react'
import { Form, Button, Card, Space } from 'antd'
import app from 'xadmin'

const FormLayout = props => {
  const { children, invalid, handleSubmit, submitting } = props
  const { _t } = app.context
  const groupProps = {
    wrapperCol: {
      xs: { span: 24, offset: 0 },
      sm: { span: 18, offset: 5 }
    }
  }
  
  return (
    <Card>
      <Form onSubmit={handleSubmit}>
        {children}
        <Form.Item {...groupProps}>
          <Space>
            <Button type="primary" onClick={handleSubmit} loading={submitting} disabled={invalid}>{_t1('调试')}</Button>
            <Button onClick={() => history.back()}>{_t('Cancel')}</Button>
          </Space>
        </Form.Item>
      </Form>
    </Card>
  )
}

export default FormLayout
