import useChart from '../../components/useChart'
import "./src/RegisterMap"

const MapComponent = props => {
  if (!!props.mapName) {
    const mapName = props.mapName[2]
    props.option.geo.map = mapName
    props.option.series[0].map = mapName
  }
  if (!!props.visualMap) {
    props.option.visualMap = props.visualMap
  }
  if (!!props.geo) {
    props.option.geo.itemStyle.normal = props.geo.normal
    props.option.geo.itemStyle.emphasis = props.geo.emphasis
  }
  return useChart({ ...props })
}

export default MapComponent
