
import { echartOption as pieEchartOption } from './charts/pie'
import { echartOption as lineEchartOption } from './charts/line'
import { echartOption as barEchartOption } from './charts/bar'
import { echartOption as scatterEchartOption } from './charts/bar'

export default { 
  'echarts.option': () => {
    return { 
      baseOption: {
        pie: pieEchartOption, 
        line: lineEchartOption, 
        bar: barEchartOption, 
        scatter: scatterEchartOption 
      }
    }
  }
}