
import React from 'react';
import { ModelWrap } from 'xadmin-model';
import { app, use, api } from 'xadmin';
import { Button, Popconfirm, Tooltip, Card, Empty, Modal, Form, message, Layout, Menu, Input, Dropdown, Col, Select, Checkbox } from 'antd';

const WarnType = props => {
  const { field: { schema }, input: { value, onChange } } = props
  const { settings } = app.context.store.getState()
  const ops = settings?.warning?.warningkind || []

  function handleChange(value) {
    onChange(value)
  }

  return (
    <Select
      allowClear
      value={value || undefined}
      style={{ width: '100%' }}
      placeholder={_t1(schema.title) + '...'}
      onChange={handleChange}
      options={ops.map(op => ({ label: op.name, value: op.id }))}
    >
    </Select>
  )
}

export default WarnType
