import React from 'react';
import _ from 'lodash';
import './style.css';

const mappers = {
  'warnings.confirmall': {
    method: {
      confirmAll: ({ model, dispatch }) => (e) => {
        dispatch({ type: '@@oilive/CONFIRM_ALL_WARNING', where: {} })
      },
      onProcessedAll: ({ model, dispatch }) => (e) => {
        dispatch({ type: '@@oilive/PROCESSED_ALL_WARNING', where: { type: 'processed' } })
      }
    }
  },
  'warnings.unread': {
    data: ({ state }) => ({ items: state.warning.unread }),
    event: {
      mount: ({ dispatch }) => dispatch({ type: '@@oilive/UNREAD_WARNING' })
    }
  }
}

export default mappers