import React from 'react';
import { Button, Popconfirm } from 'antd';
import { StoreWrap, use } from 'xadmin';

// @StoreWrap('warnings.confirmall')
const ProcessedBtn = props => {  
  const [ processedAll, setProcessedAll ] = use('warning.processed.all')
  const { t:_t1, i18n } = useI18n()

  confirm = () => {
    // this.props.onProcessedAll()
    setProcessedAll({ type: 'processed' })
  }

  return (
    <Popconfirm title={_t1("您确定要将全部报警信息处理？")} onConfirm={confirm} okText={_t1("处理全部报警信息")} cancelText={_t1("取消")}>
      <Button type="danger" block>
        {_t1('全部处理')}
      </Button>
    </Popconfirm>
  )
}

export default ProcessedBtn
